VertexShader.Code:

#version 110
attribute vec4 PosTC; 
attribute vec4 QuadData; 

varying vec4 InterpolatedData;
varying vec2 Texcoord;

void main( void )
{
	InterpolatedData = QuadData;
	Texcoord = PosTC.zw;
	gl_Position = vec4(PosTC.xy, -1.0, 1.0);
}

FragmentShader.Code:

#version 110
varying vec2 Texcoord;
varying vec4 InterpolatedData;

uniform sampler2D Map0;

void main(void)
{
	gl_FragColor = texture2D(Map0, Texcoord.xy);
}
