VertexShader.Code:
#version 120
varying vec2 Texcoord;

void main( void )
{
	gl_Position = vec4(gl_Vertex.xy * 2.0 - 1.0, 1.0, 1.0);
	Texcoord    = gl_Vertex.zw;
}

FragmentShader.Code:
#version 120
uniform sampler2D baseMap;

varying vec2 Texcoord;

void main( void )
{
	gl_FragColor = texture2D( baseMap, Texcoord )*vec4(0.7, 0.12, 0.15,1.0);
}
