		gl_Position = vec4( PosTex.xy * 2.0 - 1.0, 1.0, 1.0);
		//gl_Position = vec4( noise2(3.5), 1.0, 1.0);
		//gl_Position = vec4(gl_Vertex.xy, 1.0);
		Texcoord = PosTex.zw;
	}

