VertexShader.Code:
varying vec2 Texcoord;

void main( void )
{
	gl_Position = gl_Vertex*2.0 - 1.0;
	Texcoord    = gl_MultiTexCoord0.xy;
}

FragmentShader.Code:
uniform sampler2D baseMap;

varying vec2 Texcoord;

void main( void )
{
	gl_FragColor = texture2D( baseMap, Texcoord )*vec4(0.4,0.6,0.8,1.0);
}
