VertexShader.Code:
varying vec2 Texcoord;

void main( void )
{
	gl_Position = vec4(gl_Vertex.xy * 2.0 - 1.0, 1.0, 1.0);
	Texcoord    = gl_Vertex.zw;
}

FragmentShader.Code:
uniform sampler2D baseMap;

varying vec2 Texcoord;

void main( void )
{
	gl_FragColor = texture2D( baseMap, Texcoord )*vec4(1.0, 0.7, 0.7,1.0);
}
